import React, { Component } from 'react';

import { StyleSheet, Image, View } from 'react-native';

import { Text, Button, TextInput } from 'react-native-paper';

import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';

import { ColorPicker } from 'react-native-color-picker'

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  item: {
    width: wp('45%'),
    marginLeft: hp('1%'),
    marginRight: hp('1%'),
  }
});

class NewMatch extends Component {

  state = {
    team_one_color: 'red',
    team_two_color: 'purple',
  };

  handleChangeTeamOneColor(color) {
    this.setState({ team_one_color: color });
  };
  handleChangeTeamTwoColor(color) {
    this.setState({ team_two_color: color });
  };

  render () {
    return (

        <View style={styles.container}>
          <View style={styles.item}>
          <TextInput
            style={styles.item}
            label="Equipo A"
            value={this.state.match_code}
            onChangeText={text => this.handleMatchCodeChange(text)}
          />
          <ColorPicker 
            color={ this.state.team_one_color }
            onColorChange={ (color) => this.handleChangeTeamOneColor(color) }
            style={{flex: 1}}
            hideSliders={true}
          />
          </View>
          <View style={styles.item}>
          <TextInput
            style={styles.item}
            label="Equipo B"
            value={this.state.match_code}
            onChangeText={text => this.handleMatchCodeChange(text)}
          />
          <ColorPicker 
            color={ this.state.team_two_color }
            onColorChange={  (color) => this.handleChangeTeamTwoColor(color) }
            style={{flex: 1}}
            hideSliders={true}
            hideControls={true}
          />
          </View>
        </View>
    )
  }
}


export default NewMatch;
